changeSet.sql:
/* Link up analysis submission to input sample names (via sequencing files). */
/* Ignore analysis submissions where workflows supported multiple input files */
/* We also ignore single-end analyses as no supporting workflows are implemented in IRIDA */
CREATE TEMPORARY TABLE TEMP_0_18_0_analysis_submission_sample_name AS (
	SELECT aspe.analysis_submission_id, s.sampleName
		FROM analysis_submission sub
		INNER JOIN analysis_submission_sequence_file_pair aspe ON sub.id = aspe.analysis_submission_id
		INNER JOIN sample_sequencingobject sso ON aspe.sequence_file_pair_id = sso.sequencingobject_id
		INNER JOIN sample s ON s.id = sso.sample_id
		WHERE sub.workflow_id IN (
			'bac44f4b-46f9-4393-b247-de11a751410e','564a02d7-0cd8-4328-9090-dab1cff1b3f7','f73cbfd2-5478-4c19-95f9-690f3712f84d','87186c71-5c8a-4027-a9d9-b29850cebdb3',
			'e559af58-a560-4bbd-997e-808bfbe026e2','e8f9cc61-3264-48c6-81d9-02d9e84bccc7'
		) GROUP BY aspe.analysis_submission_id HAVING COUNT(*) = 1
	);

/* Link up these above sample names to all analysis_output_files */
CREATE TEMPORARY TABLE TEMP_0_18_0_output_file_sample_name AS (
	SELECT aof.id AS analysis_output_file_id, tssn.sampleName
	FROM analysis_output_file_map aofm
	INNER JOIN analysis_output_file aof ON aofm.analysisOutputFilesMap_id = aof.id
	INNER JOIN analysis_submission s ON aofm.analysis_id = s.analysis_id
	INNER JOIN TEMP_0_18_0_analysis_submission_sample_name tssn ON s.id = tssn.analysis_submission_id
);

/* Set default label for an output file to be the file name portion of the path (e.g., all text after final '/') */
UPDATE analysis_output_file SET label = SUBSTRING_INDEX(file_path, '/', -1);

/* Add the sample name as a prefix to the label of each output file if analysis submission applied to only a single sample */
UPDATE analysis_output_file aof
	INNER JOIN TEMP_0_18_0_output_file_sample_name tssn ON tssn.analysis_output_file_id = aof.id
	SET aof.label = CONCAT (
		tssn.sampleName,
		'-',
		SUBSTRING_INDEX(aof.file_path, '/', -1)
	);
